import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [technicians, setTechnicians] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [selectedTechnician, setSelectedTechnician] = useState('');
  const [bookingDate, setBookingDate] = useState('');
  const [status, setStatus] = useState('Pending');
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');
  const [cities, setCities] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [areas, setAreas] = useState([]);
  const [selectedArea, setSelectedArea] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const navigate = useNavigate();

  // Fetch the cities on component mount
  useEffect(() => {
    axios.get('http://localhost:8000/api/cities')
      .then((response) => {
        setCities(response.data);
      })
      .catch((error) => {
        console.error('Error fetching cities:', error);
      });
  }, []);

  // Fetch areas based on selected city
  useEffect(() => {
    if (selectedCity) {
      axios.get(`http://localhost:8000/api/areas/${selectedCity}`)
        .then((response) => {
          setAreas(response.data);
        })
        .catch((error) => {
          console.error('Error fetching areas:', error);
        });
    }
  }, [selectedCity]);

  // Fetch technicians based on selected area
  useEffect(() => {
    if (selectedArea) {
      axios.get(`http://localhost:8000/api/technicians/${selectedArea}`)
        .then((response) => {
          setTechnicians(response.data.technicians);
        })
        .catch((error) => {
          console.error('Error fetching technicians:', error);
        });
    }
  }, [selectedArea]);

  // Fetch user bookings on component mount
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.get('http://localhost:8000/api/bookings', {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then((response) => {
          setBookings(response.data);
        })
        .catch((error) => {
          console.error('Error fetching bookings:', error);
        });
    } else {
      navigate('/login');
    }
  }, []);

  // Handle booking form submission
  const handleBookingSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (selectedTechnician && bookingDate) {
      axios.post('http://localhost:8000/api/bookings', {
        technician_id: selectedTechnician,
        booking_date: bookingDate,
        status,
      }, {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then((response) => {
          setBookings([...bookings, response.data]);
          setErrorMessage('');
        })
        .catch((error) => {
          setErrorMessage('Error creating booking. Please try again.');
          console.error(error);
        });
    } else {
      setErrorMessage('Please select a technician and a date.');
    }
  };

  // Handle rating and review submission
  const handleReviewSubmit = (booking_id) => {
    const token = localStorage.getItem('token');
    if (rating && review) {
      axios.put(`http://localhost:8000/api/bookings/${booking_id}`, {
        status: 'Completed',
        rating,
        review,
      }, {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then((response) => {
          setBookings(bookings.map(booking => 
            booking.booking_id === booking_id ? { ...booking, status: 'Completed', rating, review } : booking
          ));
          setErrorMessage('');
        })
        .catch((error) => {
          setErrorMessage('Error updating booking. Please try again.');
          console.error(error);
        });
    } else {
      setErrorMessage('Please provide a rating and review.');
    }
  };
  const handleDeleteAccount = () => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .delete('http://localhost:8000/delete-account', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then(() => {
          setMessage('Account deleted successfully. Redirecting to signup...');
          localStorage.removeItem('token'); // Remove token on successful deletion
          setTimeout(() => {
            navigate('/signup'); // Redirect to signup page
          }, 5000);
        })
        .catch(() => {
          setMessage('Error deleting account.');
        });
    }
  };

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token
    navigate('/login'); // Redirect to login page
  };

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>

      {/* Display error messages */}
      {errorMessage && <div className="error">{errorMessage}</div>}

      <h2>Create a Booking</h2>
      <form onSubmit={handleBookingSubmit}>
        <select
          value={selectedCity}
          onChange={(e) => setSelectedCity(e.target.value)}
          required
        >
          <option value="">Select a City</option>
          {cities.map(city => (
            <option key={city.city_id} value={city.city_id}>{city.city_name}</option>
          ))}
        </select>

        <select
          value={selectedArea}
          onChange={(e) => setSelectedArea(e.target.value)}
          required
        >
          <option value="">Select an Area</option>
          {areas.map(area => (
            <option key={area.zipcode} value={area.zipcode}>{area.area}</option>
          ))}
        </select>

        <select
          value={selectedTechnician}
          onChange={(e) => setSelectedTechnician(e.target.value)}
          required
        >
          <option value="">Select a Technician</option>
          {technicians.map(technician => (
            <option key={technician.user_id} value={technician.user_id}>
              {technician.user_name} - {technician.services}
            </option>
          ))}
        </select>

        <input
          type="date"
          value={bookingDate}
          onChange={(e) => setBookingDate(e.target.value)}
          required
        />

        <button type="submit">Book Technician</button>
      </form>

      <h2>Your Bookings</h2>
      <table>
        <thead>
          <tr>
            <th>Technician</th>
            <th>Date</th>
            <th>Status</th>
            <th>Rating</th>
            <th>Review</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking) => (
            <tr key={booking.booking_id}>
              <td>{booking.technician_name}</td>
              <td>{new Date(booking.booking_date).toLocaleDateString()}</td>
              <td>{booking.status}</td>
              <td>{booking.rating || 'N/A'}</td>
              <td>{booking.review || 'N/A'}</td>
              <td>
                {booking.status === 'Pending' && (
                  <button onClick={() => handleReviewSubmit(booking.booking_id)}>
                    Complete and Rate
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
