import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './components/Home';
import Signup from './components/Signup';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import UpdateProfile from './components/UpdateProfile';
import ForgotPassword from './components/ForgotPassword'; 
import TechnicianDashboard from './components/TechnicianDashboard';       
// Uncomment when you are ready to implement these routes
// import VerifyCode from './components/VerifyCode';                
// import ResetPassword from './components/ResetPassword';          

// PrivateRoute component to protect routes
const PrivateRoute = ({ element, ...rest }) => {
  const token = localStorage.getItem('token');  // Check if user is logged in
  return token ? element : <Navigate to="/login" />;
};

function App() {
  return (
    <Router>
      <Routes>
        {/* Check token and role on initial route */}
        <Route
          path="/"
          element={
            localStorage.getItem('token') ? (
              // Check user role and redirect accordingly
              localStorage.getItem('user_role') === 'Technician' ? (
                <Navigate to="/TechnicianDashboard" />
              ) : (
                <Navigate to="/dashboard" />
              )
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route path="/home" element={<Home />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/login" element={<Login />} />

        {/* Private Routes */}
        <Route
          path="/dashboard"
          element={
            <PrivateRoute element={<Dashboard />} allowedRoles={['Admin']} />
          }
        />
        <Route
          path="/TechnicianDashboard"
          element={
            <PrivateRoute element={<TechnicianDashboard />} allowedRoles={['Technician']} />
          }
        />

        <Route path="/update-profile" element={<PrivateRoute element={<UpdateProfile />} allowedRoles={['Admin', 'Technician']} />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />  {/* Route for forgot password */}
      </Routes>
    </Router>
  );
}


export default App;
