import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [cities, setCities] = useState([]);
  const [areas, setAreas] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [selectedArea, setSelectedArea] = useState('');
  const [technicians, setTechnicians] = useState([]);

  useEffect(() => {
    // Fetch Cities
    axios.get('http://localhost:8000/api/cities').then((response) => {
      setCities(response.data);
    });
  }, []);

  const fetchAreas = (cityId) => {
    // Fetch Areas by City ID
    axios.get(`http://localhost:8000/api/areas/${cityId}`).then((response) => {
      setAreas(response.data);
      setSelectedArea(''); // Reset selected area
    });
  };

  const fetchTechnicians = (zipcode) => {
    // Fetch Technicians by Zipcode
    axios.get(`http://localhost:8000/api/technicians/${zipcode}`).then((response) => {
      if (response.data.success) {
        setTechnicians(response.data.technicians);
      }
    });
  };

  return (
    <div>
      <h1>Dashboard</h1>

      {/* City Dropdown */}
      <label>Select City:</label>
      <select
        value={selectedCity}
        onChange={(e) => {
          setSelectedCity(e.target.value);
          fetchAreas(e.target.value);
        }}
      >
        <option value="">--Select City--</option>
        {cities.map((city) => (
          <option key={city.city_id} value={city.city_id}>
            {city.city_name}
          </option>
        ))}
      </select>

      {/* Area Dropdown */}
      <label>Select Area:</label>
      <select
        value={selectedArea}
        onChange={(e) => {
          setSelectedArea(e.target.value);
          fetchTechnicians(e.target.value); // Fetch technicians for selected area
        }}
      >
        <option value="">--Select Area--</option>
        {areas.map((area) => (
          <option key={area.zipcode} value={area.zipcode}>
            {area.area}
          </option>
        ))}
      </select>

      {/* Technicians List */}
      <h2>Technicians</h2>
      {technicians.length > 0 ? (
        <ul>
          {technicians.map((tech) => (
            <li key={tech.user_id}>
              <strong>{tech.user_name}</strong> - {tech.experienced_year} years experience
              <br />
              Services: {tech.services || 'No services available'}
            </li>
          ))}
        </ul>
      ) : (
        <p>No technicians found for the selected area.</p>
      )}
    </div>
  );
};

export default Dashboard;
