import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css'; // Import the CSS file

const getCurrentDate = () => {
  const today = new Date();
  const year = today.getFullYear();
  const month = String(today.getMonth() + 1).padStart(2, '0'); // Months are 0-indexed
  const day = String(today.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
};

const Dashboard = () => {
  const [message, setMessage] = useState('');
  const [cities, setCities] = useState([]);
  const [areas, setAreas] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [selectedArea, setSelectedArea] = useState('');
  const [technicians, setTechnicians] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [selectedTechnician, setSelectedTechnician] = useState('');
  const [bookingDate, setBookingDate] = useState(getCurrentDate());
  const [status, setStatus] = useState('Pending');
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [customerDetails, setCustomerDetails] = useState({
    user_name: '',
    phone_number: '',
    email: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get('http://localhost:8000/dashboard', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setMessage(response.data.message);
        })
        .catch(() => {
          setMessage('Access denied. Please log in.');
        });

      // Fetch cities
      axios
        .get('http://localhost:8000/api/cities')
        .then((response) => {
          setCities(response.data);
        })
        .catch((error) => console.error('Error fetching cities:', error));

      // Fetch bookings
      axios
        .get('http://localhost:8000/api/bookings', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setBookings(response.data);
        })
        .catch((error) => console.error('Error fetching bookings:', error));

      // Fetch customer details
      axios
        .get('http://localhost:8000/api/customer-details', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setCustomerDetails(response.data);
        })
        .catch((error) => console.error('Error fetching customer details:', error));
    } else {
      navigate('/login');
    }
  }, [navigate]);

  const handleBookingSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (selectedTechnician && bookingDate) {
      axios
        .post(
          'http://localhost:8000/api/bookings',
          {
            technician_id: selectedTechnician,
            booking_date: bookingDate,
            status,
          },
          { headers: { Authorization: `Bearer ${token}` } }
        )
        .then((response) => {
          setBookings([...bookings, response.data]);
          setErrorMessage('');
        })
        .catch((error) => {
          setErrorMessage('Error creating booking. Please try again.');
          console.error(error);
        });
    } else {
      setErrorMessage('Please select a technician and a date.');
    }
  };

  const handleCustomerUpdate = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    axios
      .put(
        'http://localhost:8000/api/customer-details',
        customerDetails,
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then(() => {
        setMessage('Customer details updated successfully!');
        setErrorMessage('');
      })
      .catch((error) => {
        setErrorMessage('Error updating details. Please try again.');
        console.error(error);
      });
  };

  return (
    <div className="dashboard">
      <h1>Customer Dashboard</h1>
      <p>{message}</p>

      {/* Customer Details Update Form */}
      <h2>Update Your Information</h2>
      <form onSubmit={handleCustomerUpdate}>
        <input
          type="text"
          placeholder="Name"
          value={customerDetails.user_name}
          onChange={(e) =>
            setCustomerDetails({ ...customerDetails, user_name: e.target.value })
          }
          required
        />
        <input
          type="text"
          placeholder="Phone Number"
          value={customerDetails.phone_number}
          onChange={(e) =>
            setCustomerDetails({ ...customerDetails, phone_number: e.target.value })
          }
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={customerDetails.email}
          onChange={(e) =>
            setCustomerDetails({ ...customerDetails, email: e.target.value })
          }
          required
        />
        <button type="submit">Update Information</button>
      </form>

      {/* Booking Form */}
      <h2>Find and Book a Technician</h2>
      <form onSubmit={handleBookingSubmit}>
        <select value={selectedCity} onChange={(e) => setSelectedCity(e.target.value)}>
          <option value="">Select City</option>
          {cities.map((city) => (
            <option key={city.city_id} value={city.city_id}>
              {city.city_name}
            </option>
          ))}
        </select>

        <select
          value={selectedArea}
          onChange={(e) => setSelectedArea(e.target.value)}
          disabled={!selectedCity}
        >
          <option value="">Select Area</option>
          {areas.map((area) => (
            <option key={area.zipcode} value={area.zipcode}>
              {area.area}
            </option>
          ))}
        </select>

        <select
          value={selectedTechnician}
          onChange={(e) => setSelectedTechnician(e.target.value)}
          disabled={!selectedArea}
        >
          <option value="">Select Technician</option>
          {technicians.map((technician) => (
            <option key={technician.user_id} value={technician.user_id}>
              {technician.user_name} - {technician.services}
            </option>
          ))}
        </select>

        <input
          type="date"
          value={bookingDate}
          onChange={(e) => setBookingDate(e.target.value)}
          required
        />
        <button type="submit">Book</button>
      </form>

      {/* Bookings */}
      <h2>Your Bookings</h2>
      <table>
        <thead>
          <tr>
            <th>Technician</th>
            <th>Date</th>
            <th>Status</th>
            <th>Rating</th>
            <th>Review</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking) => (
            <tr key={booking.booking_id}>
              <td>{booking.technician_name}</td>
              <td>{new Date(booking.booking_date).toLocaleDateString()}</td>
              <td>{booking.status}</td>
              <td>{booking.rating || 'N/A'}</td>
              <td>{booking.review || 'N/A'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
