import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css'; // Import the CSS file

const Dashboard = () => {
  const [message, setMessage] = useState('');
  const [cities, setCities] = useState([]);
  const [areas, setAreas] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [selectedArea, setSelectedArea] = useState('');
  const [technicians, setTechnicians] = useState([]);
  const [role, setRole] = useState('');
  const [userName, setUserName] = useState('');
  const [userDetails, setUserDetails] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // Fetch Dashboard Message and User Info
      axios
        .get('http://localhost:8000/dashboard', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setMessage(response.data.message);
        })
        .catch(() => {
          setMessage('Access denied. Please log in.');
        });

      // Fetch Cities
      axios
        .get('http://localhost:8000/api/cities')
        .then((response) => {
          setCities(response.data);
        })
        .catch((error) => console.error('Error fetching cities:', error));

      // Fetch User Info to check the role
      axios
        .get('http://localhost:8000/api/user-info', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          const { role, user_name, user_id } = response.data;
          setRole(role);
          setUserName(user_name);
          setUserDetails({ ...response.data });

          // If Technician, fetch their details as well
          if (role === 'technician') {
            axios
              .get(`http://localhost:8000/technician-dashboard`, {
                headers: { Authorization: `Bearer ${token}` },
              })
              .then((response) => {
                setUserDetails(response.data.technician);
              })
              .catch((err) => console.log('Error fetching technician data:', err));
          } else if (role !== 'customer') {
            setMessage('Access denied. Only customers or technicians can access the dashboard.');
            navigate('/login'); // Redirect if the role is not Customer or Technician
          } else {
            setMessage(`Welcome, ${user_name}!`);
          }
        })
        .catch(() => {
          setMessage('Error fetching user data.');
        });
    } else {
      setMessage('Access denied. Please log in.');
    }
  }, [navigate]);

  // Fetch Areas when a city is selected
  const handleCityChange = (e) => {
    const cityId = e.target.value;
    setSelectedCity(cityId);
    axios
      .get(`http://localhost:8000/api/areas/${cityId}`)
      .then((response) => {
        setAreas(response.data);
        setSelectedArea(''); // Reset area dropdown
      })
      .catch((error) => console.error('Error fetching areas:', error));
  };

  // Fetch Technicians when an area is selected
  const fetchTechnicians = (zipcode) => {
    axios
      .get(`http://localhost:8000/api/technicians/${zipcode}`)
      .then((response) => {
        if (response.data.success) {
          setTechnicians(response.data.technicians);
        }
      })
      .catch((error) => console.error('Error fetching technicians:', error));
  };

  // Handle Search button click
  const handleSearch = () => {
    if (selectedCity && selectedArea) {
      // Fetch technicians after selecting city and area
      fetchTechnicians(selectedArea);
    } else {
      alert('Please select both city and area to search.');
    }
  };

  // Delete Account Function
  const handleDeleteAccount = () => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .delete('http://localhost:8000/delete-account', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then(() => {
          setMessage('Account deleted successfully. Redirecting to signup...');
          localStorage.removeItem('token'); // Remove token on successful deletion
          setTimeout(() => {
            navigate('/signup'); // Redirect to signup page after 5 seconds
          }, 5000);
        })
        .catch(() => {
          setMessage('Error deleting account.');
        });
    }
  };

  // Logout Function
  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token
    navigate('/login'); // Redirect to login page
  };

  // Update Technician Info Function
  const handleUpdateTechnician = () => {
    if (role === 'technician') {
      const { experienced_year, zipcode } = userDetails;
      const token = localStorage.getItem('token');
      axios
        .put(
          'http://localhost:8000/update-technician',
          { experienced_year, zipcode },
          { headers: { Authorization: `Bearer ${token}` } }
        )
        .then((response) => {
          setMessage('Technician information updated successfully!');
        })
        .catch((error) => {
          setMessage('Error updating technician information.');
        });
    }
  };

  return (
    <div className="dashboard-container">
      {/* Top Left - Dashboard Header */}
      <div className="top-left">
        <h1>{role === 'customer' ? 'Customer Dashboard' : 'Technician Dashboard'}</h1>
        <p>{message}</p>
      </div>

      {/* Top Right - Delete and Logout Buttons */}
      <div className="top-right">
        <button onClick={handleDeleteAccount}>Delete Account</button>
        <button onClick={handleLogout}>Logout</button>
      </div>

      {/* Main Content (Centered) */}
      <div className="main-content">
        {/* If the user is a customer, show the search form */}
        {role === 'customer' && (
          <div className="search-section">
            <h3>Find a Technician</h3>
            <div className="form-group">
              <label>City:</label>
              <select value={selectedCity} onChange={handleCityChange}>
                <option value="">Select City</option>
                {cities.map((city) => (
                  <option key={city.city_id} value={city.city_id}>
                    {city.city_name}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Area:</label>
              <select value={selectedArea} onChange={(e) => setSelectedArea(e.target.value)} disabled={!selectedCity}>
                <option value="">Select Area</option>
                {areas.map((area) => (
                  <option key={area.zipcode} value={area.zipcode}>
                    {area.area}
                  </option>
                ))}
              </select>
            </div>

            <button onClick={handleSearch}>Search</button>
          </div>
        )}

        {/* If the user is a technician, show their personal info */}
        {role === 'technician' && (
          <div className="technician-info">
            <h3>Your Technician Profile</h3>
            <p><strong>Experience:</strong> {userDetails.experienced_year} years</p>
            <p><strong>Location (Zipcode):</strong> {userDetails.zipcode}</p>

            <div className="form-group">
              <label>Experience (Years):</label>
              <input
                type="number"
                value={userDetails.experienced_year || ''}
                onChange={(e) => setUserDetails({ ...userDetails, experienced_year: e.target.value })}
              />
            </div>

            <div className="form-group">
              <label>Zipcode:</label>
              <input
                type="text"
                value={userDetails.zipcode || ''}
                onChange={(e) => setUserDetails({ ...userDetails, zipcode: e.target.value })}
              />
            </div>

            <button onClick={handleUpdateTechnician}>Update Profile</button>
          </div>
        )}

        {/* Technicians List */}
        {technicians.length > 0 && (
          <div className="technicians-list">
            <h2>Technicians</h2>
            <ul>
              {technicians.map((tech) => (
                <li key={tech.user_id}>
                  <strong>{tech.user_name}</strong> - {tech.experienced_year} years experience
                  <br />
                  Services: {tech.services || 'No services available'}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
