import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css'; // Import the CSS file

const getCurrentDate = () => {
  const today = new Date();
  const year = today.getFullYear();
  const month = String(today.getMonth() + 1).padStart(2, '0'); // Months are 0-indexed
  const day = String(today.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
};

const Dashboard = () => {
  const [message, setMessage] = useState('');
  const [cities, setCities] = useState([]);
  const [areas, setAreas] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [selectedArea, setSelectedArea] = useState('');
  const [technicians, setTechnicians] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [selectedTechnician, setSelectedTechnician] = useState('');
  const [technicianDetails, setTechnicianDetails] = useState({
    experience_years: 0,
    rating: 0,
    reviews_count: 0,
    booking_count: 0,
  });
  const [bookingDate, setBookingDate] = useState(getCurrentDate());
  const [status, setStatus] = useState('Pending');
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [customerDetails, setCustomerDetails] = useState({
    user_name: '',
    phone_number: '',
    email: '',
  });
  const [isUpdatingInfo, setIsUpdatingInfo] = useState(false); // For toggling update form
  const [isCustomerDetailsVisible, setIsCustomerDetailsVisible] = useState(false); // For toggling customer details visibility
  const [confirmationMessage, setConfirmationMessage] = useState(''); // Confirmation message state
  const [showRatingOptions, setShowRatingOptions] = useState(false); // To toggle the rating options

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get('http://localhost:8000/dashboard', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => setMessage(response.data.message))
        .catch(() => setMessage('Access denied. Please log in.'));

      axios
        .get('http://localhost:8000/api/customer-details', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => setCustomerDetails(response.data))
        .catch((error) => console.error('Error fetching customer details:', error));

      axios
        .get('http://localhost:8000/api/cities')
        .then((response) => setCities(response.data))
        .catch((error) => console.error('Error fetching cities:', error));

      axios
        .get('http://localhost:8000/api/bookings', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => setBookings(response.data))
        .catch((error) => console.error('Error fetching bookings:', error));
    } else {
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    if (selectedCity) {
      axios
        .get(`http://localhost:8000/api/areas/${selectedCity}`)
        .then((response) => setAreas(response.data))
        .catch((error) => console.error('Error fetching areas:', error));
    }
  }, [selectedCity]);

  useEffect(() => {
    if (selectedArea) {
      axios
        .get(`http://localhost:8000/api/technicians/${selectedArea}`)
        .then((response) => setTechnicians(response.data.technicians))
        .catch((error) => console.error('Error fetching technicians:', error));
    }
  }, [selectedArea]);

  const handleTechnicianChange = (e) => {
    const technicianId = e.target.value;
    setSelectedTechnician(technicianId);
    if (technicianId) {
      axios
        .get(`http://localhost:8000/api/technician-details/${technicianId}`)
        .then((response) => {
          setTechnicianDetails(response.data);
        })
        .catch((error) => {
          console.error('Error fetching technician details:', error);
          setTechnicianDetails({
            experience_years: 0,
            rating: 0,
            reviews_count: 0,
            booking_count: 0,
          });
        });
    }
  };
  const handleRatingClick = () => {
    setShowRatingOptions(!showRatingOptions); // Toggle the visibility of the rating options
  };

  const handleRatingSelection = (selectedRating) => {
    setRating(selectedRating);
    setShowRatingOptions(false); // Close the rating options after selecting
  };
  const handleBookingSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (selectedTechnician && bookingDate) {
      axios
        .post(
          'http://localhost:8000/api/bookings',
          { technician_id: selectedTechnician, booking_date: bookingDate, status },
          { headers: { Authorization: `Bearer ${token}` } }
        )
        .then((response) => {
          setBookings([...bookings, response.data]);
          setConfirmationMessage('Booking successful!');
          setTimeout(() => {
            setConfirmationMessage('');
          }, 3000);
        })
        .catch((error) => {
          setErrorMessage('Error creating booking. Please try again.');
          console.error(error);
        });
    } else {
      setErrorMessage('Please select a technician and a date.');
    }
  };

  const handleReviewSubmit = (booking_id) => {
    const token = localStorage.getItem('token'); // Retrieve JWT token from localStorage
    if (rating && review) {
      axios
        .put(
          `http://localhost:8000/api/bookings/${booking_id}`,
          {
            rating: rating,       // Pass rating to backend
            review: review,       // Pass review to backend
            status: 'Completed',   // Status is set to Completed
          },
          {
            headers: {
              Authorization: `Bearer ${token}`, // Attach JWT token for authentication
            },
          }
        )
        .then((response) => {
          // Handle success - Update the bookings or provide success feedback
          console.log('Review submitted successfully:', response.data);
          // Optionally, you can also update the local state or the UI after a successful review submission
          setBookings(
            bookings.map((booking) =>
              booking.booking_id === booking_id
                ? { ...booking, status: 'Completed', rating, review }
                : booking
            )
          );
          setMessage('Review submitted successfully!');
        })
        .catch((error) => {
          setErrorMessage('Error submitting review. Please try again.');
          console.error('Error submitting review:', error);
        });
    } else {
      setErrorMessage('Please provide a rating and review.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const handleDeleteAccount = () => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .delete('http://localhost:8000/delete-account', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then(() => {
          setMessage('Account deleted successfully. Redirecting to signup...');
          localStorage.removeItem('token');
          setTimeout(() => {
            navigate('/signup');
          }, 5000);
        })
        .catch(() => {
          setMessage('Error deleting account.');
        });
    }
  };

  const handleCustomerUpdate = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    axios
      .put('http://localhost:8000/api/customer-details', customerDetails, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(() => {
        setMessage('Customer details updated successfully!');
        setErrorMessage('');
        setIsUpdatingInfo(false);
      })
      .catch((error) => {
        setErrorMessage('Error updating details. Please try again.');
        console.error(error);
      });
  };

  const toggleCustomerDetails = () => {
    setIsCustomerDetailsVisible(!isCustomerDetailsVisible); // Toggle visibility
  };

  return (
    <div className="dashboard">
      <h1>Customer Dashboard</h1>
      <p>{message}</p>

      <div className="actions">
        <button onClick={handleDeleteAccount}>Delete Account</button>
        <button onClick={handleLogout}>Logout</button>
        <button onClick={() => navigate('/update-profile')}>
          Update Your Information
        </button>
      </div>

      <div className="show-details-button">
        <button onClick={toggleCustomerDetails}>
          {isCustomerDetailsVisible ? 'Hide' : 'Show'} Your Details
        </button>
      </div>

      {isCustomerDetailsVisible && (
        <div className="customer-details">
          <h2>Your Details</h2>
          <p><strong>Name:</strong> {customerDetails.user_name}</p>
          <p><strong>Phone Number:</strong> {customerDetails.phone_number}</p>
          <p><strong>Email:</strong> {customerDetails.email}</p>
        </div>
      )}

      <h2>Find and Book a Technician</h2>
      <form onSubmit={handleBookingSubmit}>
        <select value={selectedCity} onChange={(e) => setSelectedCity(e.target.value)}>
          <option value="">Select City</option>
          {cities.map((city) => (
            <option key={city.city_id} value={city.city_id}>
              {city.city_name}
            </option>
          ))}
        </select>

        <select value={selectedArea} onChange={(e) => setSelectedArea(e.target.value)}>
          <option value="">Select Area</option>
          {areas.map((area) => (
            <option key={area.area_id} value={area.area_id}>
              {area.area_name}
            </option>
          ))}
        </select>

        <select value={selectedTechnician} onChange={handleTechnicianChange}>
          <option value="">Select Technician</option>
          {technicians.map((technician) => (
            <option key={technician.id} value={technician.id}>
              {technician.name}
            </option>
          ))}
        </select>

        <input
          type="date"
          value={bookingDate}
          onChange={(e) => setBookingDate(e.target.value)}
        />

        <button type="submit">Book Technician</button>
      </form>

      {confirmationMessage && <p>{confirmationMessage}</p>}
      {errorMessage && <p className="error-message">{errorMessage}</p>}

      <h3>Your Bookings</h3>
      <ul>
        {bookings.map((booking) => (
          <li key={booking.booking_id}>
            <div>
              {booking.technician_name} - {booking.booking_date} - {booking.status}
            </div>

            {booking.status === 'Completed' && (
            <div className="review-section">
            <button onClick={handleRatingClick} className="rating-button">
              Rating
            </button>

            {showRatingOptions && (
              <div className="rating-options">
                {Array.from({ length: 10 }, (_, i) => i + 1).map((num) => (
                  <button
                    key={num}
                    onClick={() => handleRatingSelection(num)}
                    className="rating-option"
                  >
                    {num}
                  </button>
                ))}
              </div>
            )}

            <input
              type="text"
              value={rating}
              readOnly
              placeholder="Selected Rating"
            />

            <textarea
              value={review}
              onChange={(e) => setReview(e.target.value)}
              placeholder="Leave your review"
            />

            <button
              onClick={() => handleReviewSubmit(1)} // Replace `1` with actual booking_id
              className="submit-review-btn"
            >
              Submit Review
            </button>
              </div>
            )}
          </li>
        ))}
      </ul>
       {/* Display success or error message */}
       {message && <p className="message">{message}</p>}
    </div>
  );
};

export default Dashboard;
