import React, { useState, useEffect } from 'react';

const CreateTechnician = () => {
  const [services, setServices] = useState([]);
  const [cities, setCities] = useState([]);
  const [locations, setLocations] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [formData, setFormData] = useState({
    user_name: '',
    password: '',
    phone_number: '',
    email: '',
    experienced_year: '',
    location: '',
    service_names: [],
  });

  // Fetch services and cities from the backend
  useEffect(() => {
    fetch('/api/services-cities', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setServices(data.services);
        setCities(data.cities);
      })
      .catch((error) => console.error('Error fetching services or cities:', error));
  }, []);

  // Handle city selection and fetch locations under the city
  const handleCityChange = (e) => {
    const cityName = e.target.value;
    setSelectedCity(cityName);

    fetch(`/api/locations?city_name=${cityName}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    })
      .then((response) => response.json())
      .then((data) => setLocations(data.locations))
      .catch((error) => console.error('Error fetching locations:', error));
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle service selection
  const handleServiceChange = (e) => {
    const { value, checked } = e.target;
    const updatedServiceNames = checked
      ? [...formData.service_names, value]
      : formData.service_names.filter((name) => name !== value);

    setFormData({ ...formData, service_names: updatedServiceNames });
  };

  // Submit the form
  const handleSubmit = (e) => {
    e.preventDefault();

    fetch('/admin/create-technician', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          alert('Technician created successfully!');
        } else {
          alert('Error: ' + data.message);
        }
      })
      .catch((error) => console.error('Error creating technician:', error));
  };

  return (
    <div>
      <h2>Create Technician</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>User Name:</label>
          <input
            type="text"
            name="user_name"
            value={formData.user_name}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Phone Number:</label>
          <input
            type="text"
            name="phone_number"
            value={formData.phone_number}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Experience Years:</label>
          <input
            type="number"
            name="experienced_year"
            value={formData.experienced_year}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>City:</label>
          <select value={selectedCity} onChange={handleCityChange} required>
            <option value="">Select City</option>
            {cities.map((city) => (
              <option key={city.city_name} value={city.city_name}>
                {city.city_name}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Location:</label>
          <select
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            required
          >
            <option value="">Select Location</option>
            {locations.map((location) => (
              <option key={location.area} value={location.area}>
                {location.area}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Services:</label>
          {services.map((service) => (
            <div key={service.service_name}>
              <input
                type="checkbox"
                value={service.service_name}
                onChange={handleServiceChange}
              />
              {service.service_name}
            </div>
          ))}
        </div>
        <button type="submit">Create Technician</button>
      </form>
    </div>
  );
};

export default CreateTechnician;
