import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css'; // Import the CSS file
import TechnicianDashboard from './TechnicianDashboard'; // Import TechnicianDashboard
import CustomerDashboard from './CustomerDashboard'; // Import CustomerDashboard

const Dashboard = () => {
  const [message, setMessage] = useState('');
  const [userRole, setUserRole] = useState(''); // Track user role (Customer or Technician)
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get('http://localhost:8000/dashboard', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setMessage(response.data.message);
          setUserRole(response.data.role); // Set user role based on backend response
        })
        .catch(() => {
          setMessage('Access denied. Please log in.');
          navigate('/login');
        });
    } else {
      navigate('/login');
    }
  }, [navigate]);

  // Redirect based on user role
  useEffect(() => {
    if (userRole === 'technician') {
      navigate('/technician-dashboard');
    } else if (userRole === 'customer') {
      navigate('/customer-dashboard');
    }
  }, [userRole, navigate]);

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      <p>{message}</p>

      {/* Conditional rendering if the role is not determined yet */}
      {userRole === '' && <p>Loading...</p>}  {/* Show loading if the role is still being fetched */}
    </div>
  );
};

export default Dashboard;
