import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css'; // Import the CSS file
import TechnicianDashboard from './TechnicianDashboard'; // Import TechnicianDashboard
import CustomerDashboard from './CustomerDashboard'; // Import CustomerDashboard

const Dashboard = () => {
  const [message, setMessage] = useState('');
  const [userRole, setUserRole] = useState(''); // Track user role (Customer or Technician)
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get('http://localhost:8000/dashboard', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setMessage(response.data.message);
          setUserRole(response.data.role); // Set user role based on backend response
        })
        .catch(() => {
          setMessage('Access denied. Please log in.');
          navigate('/login');
        });
    } else {
      navigate('/login');
    }
  }, [navigate]);

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>
      <p>{message}</p>

      {/* Conditionally Render Technician or Customer Dashboard based on role */}
      {userRole === 'Technician' ? (
        <TechnicianDashboard />
      ) : userRole === 'Customer' ? (
        <CustomerDashboard />
      ) : (
        <p>Loading...</p> // Show loading if the role is not yet determined
      )}
    </div>
  );
};

export default Dashboard;
