import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [message, setMessage] = useState('');
  const [cities, setCities] = useState([]);
  const [areas, setAreas] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [selectedArea, setSelectedArea] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get('http://localhost:8000/dashboard', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setMessage(response.data.message);
        })
        .catch(() => {
          setMessage('Access denied. Please log in.');
        });

      // Fetch cities on component load
      axios
        .get('http://localhost:8000/api/cities')
        .then((response) => {
          setCities(response.data);
        })
        .catch((error) => console.error('Error fetching cities:', error));
    } else {
      setMessage('Access denied. Please log in.');
    }
  }, []);

  // Fetch areas when a city is selected
  const handleCityChange = (e) => {
    const cityId = e.target.value;
    setSelectedCity(cityId);
    axios
      .get(`http://localhost:8000/api/areas/${cityId}`)
      .then((response) => {
        setAreas(response.data);
        setSelectedArea(''); // Reset area dropdown
      })
      .catch((error) => console.error('Error fetching areas:', error));
  };

  // Handle search button click
  const handleSearch = () => {
    if (selectedCity && selectedArea) {
      navigate('/results', { state: { cityId: selectedCity, area: selectedArea } });
    } else {
      alert('Please select both city and area to search.');
    }
  };

  // Delete Account Function
  const handleDeleteAccount = () => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .delete('http://localhost:8000/delete-account', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then(() => {
          setMessage('Account deleted successfully. Redirecting to signup...');
          localStorage.removeItem('token'); // Remove token on successful deletion
          setTimeout(() => {
            navigate('/signup'); // Redirect to signup page after 5 seconds
          }, 5000);
        })
        .catch(() => {
          setMessage('Error deleting account.');
        });
    }
  };

  // Logout Function
  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token
    navigate('/login'); // Redirect to login page
  };

  return (
    <div>
      <h1>Customer Dashboard</h1>
      <p>{message}</p>

      {/* Search Section */}
      <div>
        <h3>Find a Technician</h3>
        <div>
          <label>City:</label>
          <select value={selectedCity} onChange={handleCityChange}>
            <option value="">Select City</option>
            {cities.map((city) => (
              <option key={city.city_id} value={city.city_id}>
                {city.city_name}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Area:</label>
          <select value={selectedArea} onChange={(e) => setSelectedArea(e.target.value)} disabled={!selectedCity}>
            <option value="">Select Area</option>
            {areas.map((area) => (
              <option key={area.zipcode} value={area.zipcode}>
                {area.area}
              </option>
            ))}
          </select>
        </div>

        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Account Management Section */}
      <div>
        <button onClick={handleDeleteAccount}>Delete Account</button>
        <button onClick={handleLogout}>Logout</button>
      </div>
    </div>
  );
};

export default Dashboard;
