import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';  // Import the CSS file

const Dashboard = () => {
  const [message, setMessage] = useState('');
  const [cities, setCities] = useState([]);
  const [areas, setAreas] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [selectedArea, setSelectedArea] = useState('');
  const [technicians, setTechnicians] = useState([]);
  const [booking, setBooking] = useState({ technician_id: '', status: 'Pending', rating: '', review: '' });
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // Fetch Dashboard Message
      axios
        .get('http://localhost:8000/dashboard', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setMessage(response.data.message);
        })
        .catch(() => {
          setMessage('Access denied. Please log in.');
        });

      // Fetch Cities
      axios
        .get('http://localhost:8000/api/cities')
        .then((response) => {
          setCities(response.data);
        })
        .catch((error) => console.error('Error fetching cities:', error));
    } else {
      setMessage('Access denied. Please log in.');
    }
  }, []);

  // Fetch Areas when a city is selected
  const handleCityChange = (e) => {
    const cityId = e.target.value;
    setSelectedCity(cityId);
    axios
      .get(`http://localhost:8000/api/areas/${cityId}`)
      .then((response) => {
        setAreas(response.data);
        setSelectedArea(''); // Reset area dropdown
      })
      .catch((error) => console.error('Error fetching areas:', error));
  };

  // Fetch Technicians when an area is selected
  const fetchTechnicians = (zipcode) => {
    axios
      .get(`http://localhost:8000/api/technicians/${zipcode}`)
      .then((response) => {
        if (response.data.success) {
          setTechnicians(response.data.technicians);
        }
      })
      .catch((error) => console.error('Error fetching technicians:', error));
  };

  // Handle Search button click
  const handleSearch = () => {
    if (selectedCity && selectedArea) {
      // Fetch technicians after selecting city and area
      fetchTechnicians(selectedArea);
    } else {
      alert('Please select both city and area to search.');
    }
  };

  // Handle Book button click
  const handleBook = (technician_id, technician_phone) => {
    const token = localStorage.getItem('token');
    const customer_id = localStorage.getItem('user_id'); // Assuming user_id is stored in localStorage

    if (token && customer_id) {
      const bookingData = {
        customer_id,
        technician_id,
        booking_date: new Date().toISOString().split('T')[0], // Get current date in YYYY-MM-DD format
        status: 'Pending',
        rating: booking.rating,
        review: booking.review,
      };

      // Call backend to create booking
      axios
        .post('http://localhost:8000/api/bookings', bookingData, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          alert('Booking created successfully!');
        })
        .catch((error) => {
          console.error('Error creating booking:', error);
        });
    }
  };

  // Handle the change in booking status (if customer wants to change status after booking)
  const handleChangeStatus = (bookingId, newStatus) => {
    const token = localStorage.getItem('token');

    if (token) {
      axios
        .put(`http://localhost:8000/api/bookings/${bookingId}`, { status: newStatus }, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          alert('Booking status updated successfully!');
        })
        .catch((error) => {
          console.error('Error updating booking status:', error);
        });
    }
  };

  // Delete Account Function
  const handleDeleteAccount = () => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .delete('http://localhost:8000/delete-account', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then(() => {
          setMessage('Account deleted successfully. Redirecting to signup...');
          localStorage.removeItem('token'); // Remove token on successful deletion
          setTimeout(() => {
            navigate('/signup'); // Redirect to signup page after 5 seconds
          }, 5000);
        })
        .catch(() => {
          setMessage('Error deleting account.');
        });
    }
  };

  // Logout Function
  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove token
    navigate('/login'); // Redirect to login page
  };

  return (
    <div className="dashboard-container">
      {/* Top Left - Customer Dashboard and Welcome Message */}
      <div className="top-left">
        <h1>Customer Dashboard</h1>
        <p>{message}</p>
      </div>

      {/* Top Right - Delete and Logout Buttons */}
      <div className="top-right">
        <button onClick={handleDeleteAccount}>Delete Account</button>
        <button onClick={handleLogout}>Logout</button>
      </div>

      {/* Main Content (Centered) */}
      <div className="main-content">
        {/* Search Section */}
        <div className="search-section">
          <h3>Find a Technician</h3>
          <div className="form-group">
            <label>City:</label>
            <select value={selectedCity} onChange={handleCityChange}>
              <option value="">Select City</option>
              {cities.map((city) => (
                <option key={city.city_id} value={city.city_id}>
                  {city.city_name}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Area:</label>
            <select value={selectedArea} onChange={(e) => setSelectedArea(e.target.value)} disabled={!selectedCity}>
              <option value="">Select Area</option>
              {areas.map((area) => (
                <option key={area.zipcode} value={area.zipcode}>
                  {area.area}
                </option>
              ))}
            </select>
          </div>

          <button onClick={handleSearch}>Search</button>
        </div>

        {/* Technicians List */}
        {technicians.length > 0 && (
          <div className="technicians-list">
            <h2>Technicians</h2>
            <ul>
              {technicians.map((tech) => (
                <li key={tech.user_id}>
                  <strong>{tech.user_name}</strong> - {tech.experienced_year} years experience
                  <br />
                  Services: {tech.services || 'No services available'}
                  <br />
                  Phone: {tech.phone_number}
                  <br />
                  <button onClick={() => handleBook(tech.user_id, tech.phone_number)}>Book</button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
