import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
  const [message, setMessage] = useState('');
  const [cities, setCities] = useState([]);
  const [areas, setAreas] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [selectedArea, setSelectedArea] = useState('');
  const [technicians, setTechnicians] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [selectedTechnician, setSelectedTechnician] = useState('');
  const [bookingDate, setBookingDate] = useState('');
  const [status, setStatus] = useState('Pending');
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // Fetch dashboard data
      axios
        .get('http://localhost:8000/dashboard', { headers: { Authorization: `Bearer ${token}` } })
        .then((response) => setMessage(response.data.message))
        .catch(() => setMessage('Access denied. Please log in.'));

      // Fetch cities
      axios
        .get('http://localhost:8000/api/cities')
        .then((response) => setCities(response.data))
        .catch((error) => console.error('Error fetching cities:', error));

      // Fetch bookings
      axios
        .get('http://localhost:8000/api/bookings', { headers: { Authorization: `Bearer ${token}` } })
        .then((response) => setBookings(response.data))
        .catch((error) => console.error('Error fetching bookings:', error));
    } else {
      navigate('/login');
    }
  }, [navigate]);

  // Fetch areas when a city is selected
  useEffect(() => {
    if (selectedCity) {
      axios
        .get(`http://localhost:8000/api/areas/${selectedCity}`)
        .then((response) => setAreas(response.data))
        .catch((error) => console.error('Error fetching areas:', error));
    }
  }, [selectedCity]);

  // Fetch technicians when an area is selected
  useEffect(() => {
    if (selectedArea) {
      axios
        .get(`http://localhost:8000/api/technicians/${selectedArea}`)
        .then((response) => setTechnicians(response.data.technicians))
        .catch((error) => console.error('Error fetching technicians:', error));
    }
  }, [selectedArea]);

  const handleBookingSubmit = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    const currentTime = new Date();

    if (selectedTechnician && bookingDate) {
      axios
        .post(
          'http://localhost:8000/api/bookings',
          {
            technician_id: selectedTechnician,
            booking_date: bookingDate,
            booking_time: currentTime.toISOString(),
            status,
          },
          { headers: { Authorization: `Bearer ${token}` } }
        )
        .then((response) => {
          setBookings([...bookings, response.data]);
          setErrorMessage('');
          setSelectedCity('');
          setSelectedArea('');
          setSelectedTechnician('');
          setBookingDate('');
        })
        .catch(() => setErrorMessage('Error creating booking. Please try again.'));
    } else {
      setErrorMessage('Please select a technician and a date.');
    }
  };

  const handleReviewSubmit = (booking_id) => {
    const token = localStorage.getItem('token');
    if (rating && review) {
      axios
        .put(
          `http://localhost:8000/api/bookings/${booking_id}`,
          { status: 'Completed', rating, review },
          { headers: { Authorization: `Bearer ${token}` } }
        )
        .then(() => {
          setBookings(
            bookings.map((booking) =>
              booking.booking_id === booking_id
                ? { ...booking, status: 'Completed', rating, review }
                : booking
            )
          );
          setErrorMessage('');
          setRating('');
          setReview('');
        })
        .catch(() => setErrorMessage('Error updating booking. Please try again.'));
    } else {
      setErrorMessage('Please provide a rating and review.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const handleDeleteAccount = () => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .delete('http://localhost:8000/delete-account', { headers: { Authorization: `Bearer ${token}` } })
        .then(() => {
          localStorage.removeItem('token');
          navigate('/signup');
        })
        .catch(() => setMessage('Error deleting account.'));
    }
  };

  return (
    <div className="dashboard">
      <h1>Customer Dashboard</h1>
      <p>{message}</p>

      <div className="actions">
        <button onClick={handleDeleteAccount}>Delete Account</button>
        <button onClick={handleLogout}>Logout</button>
      </div>

      <h2>Find and Book a Technician</h2>
      <form onSubmit={handleBookingSubmit}>
        <select value={selectedCity} onChange={(e) => setSelectedCity(e.target.value)}>
          <option value="">Select City</option>
          {cities.map((city) => (
            <option key={city.city_id} value={city.city_id}>
              {city.city_name}
            </option>
          ))}
        </select>

        <select value={selectedArea} onChange={(e) => setSelectedArea(e.target.value)} disabled={!selectedCity}>
          <option value="">Select Area</option>
          {areas.map((area) => (
            <option key={area.zipcode} value={area.zipcode}>
              {area.area}
            </option>
          ))}
        </select>

        <select value={selectedTechnician} onChange={(e) => setSelectedTechnician(e.target.value)} disabled={!selectedArea}>
          <option value="">Select Technician</option>
          {technicians.map((technician) => (
            <option key={technician.user_id} value={technician.user_id}>
              {technician.user_name} - {technician.services}
            </option>
          ))}
        </select>

        <input type="date" value={bookingDate} onChange={(e) => setBookingDate(e.target.value)} required />
        <button type="submit" disabled={!bookingDate || !selectedTechnician}>Book</button>
      </form>

      {errorMessage && <p className="error">{errorMessage}</p>}

      <h2>Your Bookings</h2>
      <table>
        <thead>
          <tr>
            <th>Technician</th>
            <th>Date</th>
            <th>Status</th>
            <th>Rating</th>
            <th>Review</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking) => (
            <tr key={booking.booking_id}>
              <td>{booking.technician_name}</td>
              <td>{new Date(booking.booking_date).toLocaleDateString()}</td>
              <td>{booking.status}</td>
              <td>{booking.rating || 'N/A'}</td>
              <td>{booking.review || 'N/A'}</td>
              <td>
                {booking.status === 'Pending' && (
                  <div>
                    <input
                      type="number"
                      placeholder="Rating"
                      value={rating}
                      onChange={(e) => setRating(e.target.value)}
                      min="1"
                      max="5"
                    />
                    <input
                      type="text"
                      placeholder="Review"
                      value={review}
                      onChange={(e) => setReview(e.target.value)}
                    />
                    <button onClick={() => handleReviewSubmit(booking.booking_id)}>Submit</button>
                  </div>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Dashboard;
