import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CreateTechnician = () => {
  const [services, setServices] = useState([]);
  const [cities, setCities] = useState([]);
  const [areas, setAreas] = useState([]);
  const [selectedCity, setSelectedCity] = useState('');
  const [formData, setFormData] = useState({
    user_name: '',
    password: '',
    phone_number: '',
    email: '',
    experienced_year: '',
    location: '',
    service_names: [], // Array for storing selected service names
  });

  // Fetch cities using axios
  useEffect(() => {
    axios
      .get('http://localhost:8000/api/cities')
      .then((response) => setCities(response.data))
      .catch((error) => console.error('Error fetching cities:', error));
  }, []);

  // Fetch services using axios
  useEffect(() => {
    axios
      .get('http://localhost:8000/api/services')
      .then((response) => setServices(response.data))
      .catch((error) => console.error('Error fetching services:', error));
  }, []);

  // Fetch areas when a city is selected
  const handleCityChange = (e) => {
    const cityId = e.target.value;
    setSelectedCity(cityId);

    axios
      .get(`http://localhost:8000/api/areas/${cityId}`)
      .then((response) => setAreas(response.data))
      .catch((error) => console.error('Error fetching areas:', error));
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle service checkbox change
  const handleServiceChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prevState) => {
      let updatedServiceNames;
      if (checked) {
        updatedServiceNames = [...prevState.service_names, value]; // Add service to array
      } else {
        updatedServiceNames = prevState.service_names.filter(
          (service) => service !== value // Remove service from array
        );
      }
      return { ...prevState, service_names: updatedServiceNames };
    });
  };

  // Submit handler
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData); // This logs the selected services and other form data
    // Here you can submit form data to the backend, e.g., using axios.post
  };

  return (
    <div>
      <h2>Create Technician</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>User Name:</label>
          <input
            type="text"
            name="user_name"
            value={formData.user_name}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Phone Number:</label>
          <input
            type="text"
            name="phone_number"
            value={formData.phone_number}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Experience Years:</label>
          <input
            type="number"
            name="experienced_year"
            value={formData.experienced_year}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>City:</label>
          <select value={selectedCity} onChange={handleCityChange} required>
            <option value="">Select City</option>
            {cities.map((city) => (
              <option key={city.city_id} value={city.city_id}>
                {city.city_name}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Area:</label>
          <select
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            required
          >
            <option value="">Select Area</option>
            {areas.map((area) => (
              <option key={area.zipcode} value={area.area}>
                {area.area}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label>Services:</label>
          <div>
            {services.map((service) => (
              <div key={service.service_name}>
                <input
                  type="checkbox"
                  value={service.service_name}
                  checked={formData.service_names.includes(service.service_name)} // Makes the checkbox checked based on selection
                  onChange={handleServiceChange}
                />
                {service.service_name}
              </div>
            ))}
          </div>
        </div>
        <button type="submit">Create Technician</button>
      </form>
    </div>
  );
};

export default CreateTechnician;
